package java_exam;

/*
 * Class: 프로그램의 최소 실행 단위
 * 파일의 이름과 클래스 명은 동일해야한다 (자바는 대소문자를 구분한다)
 */
public class HelloWorld {

	// 메서드: 구현부
	// psvm {...}
	// HelloWorld 클래스를 실행시키는 메서드
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// "Hello, World!" 문장을 출력해보자
		// 출력을 위한 코드: System.out.println(출력 문장 혹은 수식);
		System.out.println("Hello, World!");
		System.out.println(30303030);
		System.out.println(93.324324);
		System.out.println(3 + 3);
		System.out.println(3 - 2);
		System.out.println(3 / 2); // div
		System.out.println(3 % 2); // mod
		System.out.println(3 * 3);
		// 실행 방법:	Windows: Ctrl + Shift + F11
		//			MacOS: Command + Shift + F11
	}
}
